import React, { useState } from 'react';
import Movie from './Movie';

function MovieList() {
  const [movies, setMovies] = useState([]);
  const [title, setTitle] = useState('');
  const [rating, setRating] = useState('0');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!title && rating === "0") {
      alert("Du glömde skriva in titel och betyg till filmen!");
      return;
    } else if (title === "") {
      alert("Du glömde skriva in titel till filmen!");
      return;
    } else if (rating === "0") {
      alert("Du glömde fylla i betyg");
      return;
    }
    const movie = { title, rating };
    setMovies((prevMovies) => [...prevMovies, movie]);
    setTitle('');
    setRating('0');
  };

  const handleDelete = (title) => {
    setMovies((prevMovies) => prevMovies.filter((movie) => movie.title !== title));
  };

  const sortByTitle = () => {
    const sortedMovies = [...movies].sort((a, b) => a.title.localeCompare(b.title));
    setMovies(sortedMovies);
  };

  const sortByRating = () => {
    const sortedMovies = [...movies].sort((a, b) => b.rating - a.rating);
    setMovies(sortedMovies);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <fieldset>
          <legend>Lägg till en film</legend>

          <label for="title-field">Titel:</label>
          <input type="text" id="title-field" class="form-control" value={title} onChange={(e) => setTitle(e.target.value)} />

          <label for="rating-field">Betyg:</label>
          <select id="rating-field" class="form-control" value={rating} onChange={(e) => setRating(e.target.value)}>
            <option value="0">Välj betyg här...</option>
            <option value="1">1</option>
            <option value="2">2</option>
            <option value="3">3</option>
            <option value="4">4</option>
            <option value="5">5</option>
          </select>

          <input type="submit" class="btn btn-success mt-3" value="Spara film" />
        </fieldset>
      </form>

      <hr />

      <h2>Filmer</h2>
      <button onClick={sortByTitle}>Sortera filmerna i alfabetisk ordning</button>
      <button onClick={sortByRating}>Sortera filmera </button>

      <ul id="movies">
        {movies.map((movie, index) => (
          <Movie key={index} movie={movie} handleDelete={handleDelete} />
        ))}
      </ul>
    </div>
  );
}

export default MovieList;
